import React, {useContext, useEffect} from 'react';
import WorkerCard from './WorkerCard';
import {Context} from "../index";

import {observer} from "mobx-react-lite";
import {fetchWorker, fetchWorkPermit, fetchWorkStatus} from "../http/ShiftsAPI";

const ShiftList = observer(({ workers }) => {
    const {shifts}=useContext(Context)
    useEffect(()=>{
        fetchWorker().then(data =>shifts.setWorker(data))
        fetchWorkPermit().then(data =>shifts.setWorkerPermir(data))
        fetchWorkStatus().then(data =>shifts.setWorkerStatus(data))
    },[])
    const handleDelete = (workerId) => {
        // логика удаления работника
    };

    return (
        <div>
            {shifts.worker.map(worker => (
                <WorkerCard
                    key={worker.id}
                    firstName={worker.firstname}
                    surname ={worker.surname}
                    onDelete={() => handleDelete(worker.id)}
                />
            ))}
        </div>
    );
});

export default ShiftList;
