import {makeAutoObservable} from "mobx";

export default class UserM{
    constructor() {
        this._isAuth = false
        this._user = {}
        makeAutoObservable(this)
    }

    setIsAuth(bool){
        this._isAuth = bool
    }

    setUser(user){
        this._user = user
    }

    get IsAuth(){
        return this._isAuth
    }
    get user() {
        return this._user
    }

    get userId(){
        return this._user.id;
    }
}
